mapper.update:
		UPDATE
			BOARD.CONTENTS
		SET
			CONTENT_TITLE = #{title}
			,CONTENT_CONTENT = #{content}
			,CONTENT_REGDATE = CURRENT_TIMESTAMP
		WHERE
			CONTENT_NO = #{contentNo}

UPDATE
	BOARD.FILES
SET
	FILES_ORG_NAME = #{orgName}
	,FILES_SAVE_NAME = #{saveName}
	,FILES_FILE_PATH = #{filePath}
	,FILES_FILE_SIZE = #{fileSize}
WHERE
	FILES_NO = #{filesNo}
